import React, { useState } from "react";
import Input from "../Input";
import Button from "../Button";
import { CardLogin, Container, ParStyled } from "./style";
import { Link } from "react-router-dom";
import * as yup from 'yup';




export default function Transferencia(){
  const [ data, setData] = useState({
    userNameCredited: '',
    money: '',
    password: ''
  })
  const validate = async ()=>{
    const schema = yup.object().shape({
      password: yup.string('Favor preencher com senha uma válida')
        .min(6, 'Digite uma senha com no mínimo 6 caracteres')
        .required('Favor preencher com uma senha válida'),
      money: yup.number('Favor preencher com um valor válido')
        .required('Favor preencher com um valor válido!'),
      userNameCredited: yup.string('Favor inserir um nome válido')
        .min(3, 'Favor inserir um nome válido')
        .required('Favor inserir um nome'),
    })
    try{
      await schema.validate(data)
      return true
    }
    catch(err){
      setData(err.errors)
      return false
    }
   }
  return(
    <Container>


      <CardLogin>
      <h1>Área de Transferência</h1>
      <label>
        Nome do beneficiário
      <Input/>
      </label>
      <label>
        Valor
      <Input/>
      </label>
      <label>
       Digite sua senha
      <Input/>
      </label>
      <Button>Transferir</Button>
      <ParStyled>Faça sua transferência para usuários da plataforma</ParStyled>
      <ParStyled><Link to="/usuario">Voltar para área do usuário</Link></ParStyled>
      </CardLogin>

    </Container>
  )
}
