import React, { createContext, useState, useEffect} from "react";
import { useNavigate } from "react-router-dom";
import { Api } from "../Services";

export const AuthContext = createContext()

export default function AuthProvider({children}){
  const navigate = useNavigate()
  const [ status, setStatus] = ('')
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); 

  useEffect(()=>{
    const handleLocalStorage = localStorage.getItem('userToken')
    if(handleLocalStorage){
    setUser(JSON.parse(handleLocalStorage))
    }
    setLoading(false)
  },[])
  const login = async (username, password) =>{
  const init = {
    method: 'POST', 
    headers: {
      "Content-Type":'application/json'
    },
    body: JSON.stringify({
      username: username,
      password: password
    })
  }
  try{
  const response = await fetch("http://localhost:8080/login", init)
  console.log(response)
  const data = await response.json()
  const { token } = data
  localStorage.setItem('userToken', JSON.stringify(token))
  localStorage.setItem('user', JSON.stringify(data.username))
  Api.defaults.headers.Authorization = `Bearer ${token}`;
  setUser(data.username)
  return navigate('/usuario')
  }
  catch(err){
    setStatus(err)
  }
} 

const logout = ()=>{
  localStorage.removeItem('userToken')
  localStorage.removeItem('user')
  Api.defaults.headers.Authorization = `Bearer ${null}`;
  setUser(null)
  navigate('/login')
}
  return(
    <AuthContext.Provider value={{authenticated: !!user, login, logout, user, loading, status, setStatus }}>
      {children}
    </AuthContext.Provider>
  )
}
