import React, { useState } from "react";
import Input from "../Input";
import Button from "../Button";
import { CardLogin, Container, ParStyled, ResulCadastro } from "./style";
import { Link } from "react-router-dom";




export default function Login(){
  const [user, setUser] = useState({
    username: '',
    password: ''
  })
  const [data, setData] = useState('')

  const handleClick = async ()=>{
    console.log(user)
    const init = {
      method: 'POST', 
      headers: {
        "Content-Type":'application/json'
      },
      body: JSON.stringify({
        username: user.username,
        password: user.password
      })
    }
    const response = await fetch("http://localhost:8080/login", init)
    .then((res)=> res.json())
    // .then((res)=> setData(res))
    .then((res)=> console.log(res))
    .catch((err)=> setData(err))

    return console.log(response)
  }
  return(
    <Container>


      <CardLogin>
      <h1>Login</h1>
      <ResulCadastro>{data}</ResulCadastro>
      <label>
        Nome de usuário
      <Input
      type={'text'}
      name={'username'}
      value={user.username}
      onChange={(e)=> {
        setUser({
          username: e.target.value,
          password: user.password
        })
      }}
      />
      </label>
      <label>
        Senha
      <Input
      type={'password'}
      name={'password'}
      value={user.password}
      onChange={(e)=>{
        setUser({
          username: user.username,
          password: e.target.value
        })
      }}
      />
      </label>
      <Button
      type={'submit'}
      onClick={handleClick}
      >
        Login
      </Button>
      <ParStyled>Ainda não tem conta na NG Cash? <Link to={'/cadastro'}>Cadastrar</Link></ParStyled>
      <ParStyled>Cadastre agora e ganhe um bônus de R$1.000,00 para transferir entre os usuários da plataforma</ParStyled>
      </CardLogin>

    </Container>
  )
}
