import React, { useEffect, useState } from "react";
import { Card, CardTransf, Container, IdTransf, ParIdTrans, ParInfo, SaldoTranf } from "./style";
import { Link } from "react-router-dom";
import {GiPayMoney} from "react-icons/gi"
import { Api, Usuario } from "../../Services";

//Nome
//Balance
//Histórico de transferência


export default function Transferencia(){
  const [ name, setName ] = useState('')
  const [ balance, setBalance ] = useState('')
  useEffect(()=>{
    Api
    .get('/usuario')
    .then(res=>setBalance(res.data))
    .catch(err =>console.log(err))
    const getName = localStorage.getItem('user')
    setName(JSON.parse(getName))
  },[])




  return(
    <Container>

      {/* {balance} */}
      <Card>
        <h1>Olá, {name}!</h1>

          <SaldoTranf>
            <h2>Conta: R$1.000,00</h2>
            <Link to={'/transferencia'}>
            Transferir
              {/* <GiPayMoney/> */}
            </Link>
          </SaldoTranf>

        <h2>Histórico</h2>
          <CardTransf>
            <GiPayMoney/>
            <IdTransf>
              <ParIdTrans>Transferência recebida <span>10/10/2022</span></ParIdTrans>
              <ParInfo>Marcos Aurélio dos Santos Júnior</ParInfo>
              <ParInfo>R$20,00</ParInfo>
            </IdTransf>
          </CardTransf>

      </Card>

    </Container>
  )
}
