import styled from "styled-components";


export const CardLogin = styled.section`
  display: flex;
  flex-direction: column;
  gap: 16px;
  padding: 5%;
  width: 50%;
  h1{
    font-size: 36px;
    margin-bottom: 20px;
  }
  @media screen and (max-width: 900px) {
      width:85%;
  }

`
export const Container = styled.section`
  display: flex;
  flex-direction: row;
  justify-content: center;
  gap: 16px;
  width: 100%;
`
export const SaldoTranf = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  svg{
    width: 30px;
    height: 30px;
    border-radius: 50%;
    border: solid 0.01px #2DA44E;
    margin:12px
  }
  a{
    /* text-decoration: none; */
    font-weight: bolder;
    color: #2DA44E;
    width: 200px;
    display: flex;
    align-items: center;
    justify-content: space-around;
  }
`
