import React, { useEffect, useState } from "react";
import { Card, CardTransf, Container, IdTransf, ParIdTrans, ParInfo, SaldoTranf } from "./style";
import { Link } from "react-router-dom";
import {GiPayMoney} from "react-icons/gi"
import { User } from "../../Services";


export default function Usuario(){
  // const [ name, setName ] = useState('')
  const [ balance, setBalance ] = useState([])
  const [ loading, setLoading ] = useState(true)
  useEffect(()=>{
    (async ()=>{
      const response = await Usuario()
      setBalance(response.data) 
      setLoading(false)})()
  },[])
  // console.log(balance)

  if(loading){
    return(
      <div>...Carregando</div>

    )
  }


  return(
    <Container>
      {console.log(balance)}
      {/* {balance} */}
      <Card>
        <h1>Olá, {}!</h1>

          <SaldoTranf>
            <h2>Conta: R$1.000,00</h2>
            <Link to={'/transferencia'}>
            Transferir
              {/* <GiPayMoney/> */}
            </Link>
          </SaldoTranf>

        <h2>Histórico</h2>
          <CardTransf>
            <GiPayMoney/>
            <IdTransf>
              <ParIdTrans>Transferência recebida <span>10/10/2022</span></ParIdTrans>
              <ParInfo>Marcos Aurélio dos Santos Júnior</ParInfo>
              <ParInfo>R$20,00</ParInfo>
            </IdTransf>
          </CardTransf>

      </Card>

    </Container>
  )
}
