import React, { useEffect, useState } from "react";
import { Card, CardTransf, Container, IdTransf, ParIdTrans, ParInfo, SaldoTranf } from "./style";
import { Link } from "react-router-dom";
import {GiPayMoney} from "react-icons/gi"

//Nome
//Balance
//Histórico de transferência


export default function Transferencia(){
  const [ name, setName ] = useState('')
  const [ balance, setBalance ] = useState('')
  useEffect(async ()=>{
      const init = {
        method: 'GET',
        headers: {
          "Content-Type": 'application/json'
        },
      }
      const response = await fetch('http://localhost:8080/usuario', init)
    .then((res=> res.json() ))
    .catch((error)=> {
      const data = error.json()
      return console.log(data) 
    })
    const getName = localStorage.getItem('user')
    setName(JSON.parse(getName))
  },[])


  return(
    <Container>


      <Card>
        <h1>Olá, {name}!</h1>

          <SaldoTranf>
            <h2>Conta: R$1.000,00</h2>
            <Link to={'/transferencia'}>
            Transferir
              {/* <GiPayMoney/> */}
            </Link>
          </SaldoTranf>

        <h2>Histórico</h2>
          <CardTransf>
            <GiPayMoney/>
            <IdTransf>
              <ParIdTrans>Transferência recebida <span>10/10/2022</span></ParIdTrans>
              <ParInfo>Marcos Aurélio dos Santos Júnior</ParInfo>
              <ParInfo>R$20,00</ParInfo>
            </IdTransf>
          </CardTransf>

      </Card>

    </Container>
  )
}
