        {console.log(balance)}
        <h1>Olá, {}!</h1>

          <SaldoTranf>
            <h2>Seu saldo é: R$1.000,00</h2>
            <Link to={'/transferencia'}>
            Transferir
              {/* <GiPayMoney/> */}
