import styled from "styled-components";


export const ButtonStyled = styled.button`
  width: 100%;
  height: 40px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 18px;
  background-color: #2DA44E;
  color: #fff;
  font-weight: bolder;
  cursor: pointer;
  border: none;
  border-radius: 7px;
  margin-bottom: 16px;
  margin-top: 15px;
  opacity: 0.9;
  :hover{
    opacity: 1;
  }
`
